#[bench]
fn bench_bls12_381_pairing_0_rust(b: &mut Bencher) {
    use eth_pairings::public_interface::PairingApi;

    let calldata = hex::decode(BLS12_PAIR_0).unwrap();

    b.iter(|| {
        eth_pairings::public_interface::PublicPairingApi::pair(&calldata).expect_err("must be an error");
    });
}

#[bench]
fn bench_bls12_381_pairing_0_cpp(b: &mut Bencher) {
    let mut calldata = vec![7u8];

    let payload = hex::decode(BLS12_PAIR_0).unwrap();

    calldata.extend(payload);

    b.iter(|| {
        eth_pairings_cpp::run(&calldata).expect_err("must be an error");
    });
}